hudson.tasks.Shell.command:
#!/bin/bash set -o errexit
currentDir="$(pwd)" tmpDir="$(mktemp -d)" trap "{ rm -rf ${tmpDir}; }" EXIT echo "Cloning to [${tmpDir}] and building the releaser" git clone -b "${RELEASER_BRANCH:-main}" --single-branch https://github.com/spring-cloud/spring-cloud-release-tools.git "${tmpDir}" pushd "${tmpDir}" rm -rf ~/.m2/repository/org/springframework/cloud ROOT_VIEW="Spring%20Cloud" CURRENT_VIEW="Releaser" echo "Building the releaser. If the build fails after this then it means that the releaser failed to get built. Then please check the build's workspace under [.git/releaser.log] for logs. You can click here to see it [${JENKINS_URL}/view/${ROOT_VIEW}/view/${CURRENT_VIEW}/job/${JOB_NAME}/ws/.git/releaser.log]" ./mvnw clean install -DskipTests -am -pl :reactor > "${currentDir}/.git/releaser.log" popd releaserJarLocation="${tmpDir}/projects/reactor/target/" additionalParams="" if [[ $RELEASE_VERSION != "" ]]; then echo "Found the release version parameter. Will use the properties file to set the versions" version=$( echo "$RELEASE_VERSION" | tr '[:upper:]' '[:lower:]' | tr '.' '_' ) configFile="${version}.properties" configUrl="${RELEASER_CONFIG_URL}/${RELEASER_CONFIG_BRANCH}/${configFile}" echo "Downloading the configuration properties file from [${configUrl}]" rm -rf config && mkdir -p ${releaserJarLocation} && curl --fail "${configUrl}" -o ${releaserJarLocation}/application.properties echo "Cause nobody remembers to set the releaser branch properly, I'll do it for you" if [[ $version == *"hoxton"* ]]; then echo "You're using the Hoxton release train, I'm switching the releaser branch [$RELEASER_BRANCH] to [1.0.x] !" RELEASER_BRANCH="1.0.x" fi
versions="--$( sed '{:q;N;s/\n/ --/g;t q}' ${releaserJarLocation}/application.properties )" additionalParams="--releaser.git.fetch-versions-from-git=false ${versions}" fi echo "Run the releaser against the project" echo "Checking out branch" git checkout $BRANCH echo "Releasing the project"
set +x git config user.name "spring-buildmaster" git config user.email "buildmaster@springframework.org" git config credential.helper "store --file=/tmp/gitcredentials" echo "https://$GITHUB_REPO_USERNAME:$GITHUB_REPO_PASSWORD@github.com" > /tmp/gitcredentials export SPRING_CLOUD_STATIC_REPO="https://$GITHUB_REPO_USERNAME:$GITHUB_REPO_PASSWORD@github.com/spring-cloud/spring-cloud-static.git" trap "{ rm -f /tmp/gitcredentials; }" EXIT set -x
export ORG_GRADLE_PROJECT_releaserDryRun=${DRY_RUN}
set +x SPRING_CLOUD_RELEASE_REPO="https://github.com/spring-cloud/spring-cloud-release.git" SYSTEM_PROPS="-Dgpg.secretKeyring="$FOO_SEC" -Dgpg.publicKeyring="$FOO_PUB" -Dgpg.passphrase="$FOO_PASSPHRASE" -DSONATYPE_USER="$SONATYPE_USER" -DSONATYPE_PASSWORD="$SONATYPE_PASSWORD"" java ${RELEASER_ADDITIONAL_PROPS} -Dreleaser.git.username="$GITHUB_REPO_USERNAME" -Dreleaser.git.password="$GITHUB_REPO_PASSWORD" -jar ${releaserJarLocation}/reactor*SNAPSHOT.jar --releaser.post-release-tasks-only=${RELEASER_POST_RELEASE_ONLY} --releaser.meta-release.release-train-project-name=${RELEASER_META_RELEASE_RELEASE_TRAIN_PROJECT_NAME} --releaser.git.release-train-bom-url=${RELEASER_GIT_RELEASE_TRAIN_BOM} --releaser.pom.this-train-bom=${RELEASER_POM_THIS_TRAIN} --releaser.pom.branch=${RELEASER_POM_BRANCH} --releaser.maven.wait-time-in-minutes=180 --releaser.maven.system-properties="${SYSTEM_PROPS}" --full-release --dry-run=${DRY_RUN} --releaser.sagan.update-sagan=${RELEASER_SAGAN_UPDATE} --interactive=false $additionalParams || exit 1 rm -rf /tmp/gitcredentials
